// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;
/**
 * ----------------------------------------------------------------------------------------------------------------
 * ---------                             -----
 * ---------                    -----
 * ---------                    -----
 * ---------                  -----
 * ---------            -----
 * ---------                     -----
 * ----------------------------------------------------------------------------------------------------------------
 * https://github.com/coinbase/build-onchain-apps
 *
 * Disclaimer: The provided Solidity contracts are intended solely for educational purposes and are
 *   not warranted for any specific use. They have not been audited and may contain vulnerabilities, hence should
 *   not be deployed in production environments. Users are advised to seek professional review and conduct a
 *   comprehensive security audit before any real-world application to mitigate risks of financial loss or other
 *   consequences. The author(s) disclaim all liability for any damages arising from the use of these contracts.
 *   Use at your own risk, acknowledging the inherent risks of smart contract technology on the blockchain.
 *
 */

/**
 * @title Memos
 * @dev Memo struct
 */
struct Memo {
    uint numCoffees;
    string userName;
    string twitterHandle;
    string message;
    uint256 time;
    address userAddress;
}

/**
 * @title BuyMeACoffee
 * @dev BuyMeACoffee contract to accept donations and for our users to leave a memo for us
 */
contract BuyMeACoffee {
    address payable public owner;
    uint256 public price;
    Memo[] public memos;

    error InsufficientFunds();
    error InvalidArguments(string message);
    error OnlyOwner();

    event BuyMeACoffeeEvent(address indexed buyer, uint256 price);
    event NewMemo(address indexed userAddress, uint256 time, uint numCoffees, string userName, string twitterHandle, string message);

    constructor() {
        owner = payable(msg.sender);
        price = 0.0001 ether;
    }

    /**
     * WRITE FUNCTIONS *************
     */

    /**
     * @dev Function to buy a coffee
     * @param  userName The name of the user
     * @param  twitterHandle The Twitter handle of the user
     * @param  message The message of the user
     * (Note: Using calldata for gas efficiency)
     */
    function buyCoffee(uint numCoffees, string calldata userName, string calldata twitterHandle, string calldata message) public payable {
        if (msg.value < price*numCoffees) {
            revert InsufficientFunds();
        }

        emit BuyMeACoffeeEvent(msg.sender, msg.value);

        if (bytes(userName).length == 0 && bytes(message).length == 0) {
            revert InvalidArguments("Invalid userName or message");
        }

        memos.push(Memo(numCoffees, userName, twitterHandle, message, block.timestamp, msg.sender));

        emit NewMemo(msg.sender, block.timestamp, numCoffees, userName, twitterHandle, message);
    }

    /**
     * @dev Function to remove a memo
     * @param  index The index of the memo
     */
    function removeMemo(uint256 index) public {
        if (index >= memos.length) {
            revert InvalidArguments("Invalid index");
        }

        Memo memory memo = memos[index];

        // if operation isnt sent from the same user or the owner, then not allowed
        if (memo.userAddress != msg.sender && msg.sender != owner) {
            revert InvalidArguments("Operation not allowed");
        }

        Memo memory indexMemo = memos[index];
        memos[index] = memos[memos.length - 1];
        memos[memos.length - 1] = indexMemo;
        memos.pop();
    }

    /**
     * @dev Function to modify a memo
     * @param  index The index of the memo
     * @param  message The message of the memo
     */
    function modifyMemoMessage(uint256 index, string memory message) public {
        if (index >= memos.length) {
            revert InvalidArguments("Invalid index");
        }

        Memo memory memo = memos[index];

        if (memo.userAddress != msg.sender || msg.sender != owner) {
            revert InvalidArguments("Operation not allowed");
        }

        memos[index].message = message;
    }

    /**
     * @dev Function to withdraw the balance
     */
    function withdrawTips() public {
        if (msg.sender != owner) {
            revert OnlyOwner();
        }

        if (address(this).balance == 0) {
            revert InsufficientFunds();
        }

        (bool sent,) = owner.call{value: address(this).balance}("");
        require(sent, "Failed to send Ether");
    }

    /**
     * READ FUNCTIONS *************
     */

    /**
     * @dev Function to get the memos
     */
    function getMemos() public view returns (Memo[] memory) {
        return memos;
    }

    /**
     * @dev Recieve function to accept ether
     */
    receive() external payable {}
}

contracts/ChamaGroup.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;


contract ChamaGroup {
    // Address of the contract owner
    address private immutable owner;

    // Counter for generating group IDs
    uint private nextId = 1;

    // Enum representating the status of the group
    enum STATUS {
        ACTIVE,
        DELETED
    }

    // Struct to define the structure of the chama groups
    struct Group {
        uint id;
        address creator;
        string name;
        string description;
        STATUS status;
        uint totalContributions;
        address[] contributors;
        uint[] contributionAmounts;
    }

    // Mapping to efficiently store groups by ID
    mapping(uint => Group) public groups;

    // Mapping to track which groups a contributor has participated in
    mapping(address => uint[]) private contributorGroups;

    event GroupCreated(uint indexed groupId, address groupCreator, string name, STATUS status);
    event GroupDeleted(uint indexed groupId, address groupCreator, STATUS status);
    event ContributorAdded(uint indexed groupId, address indexed newContributor);


    // Contract
    constructor(){
        owner = msg.sender;
    }

    // Modifier to allow only the contract owner to perform certain actions
    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can perform this action.");
        _;
    }


     // Function to create a new chama group
    function createGroup(
        string memory _name, // name of the chama group
        string memory _description // description of the chama group
    ) public {
        // validation checks for input parameters
        require(bytes(_name).length > 0, 'Name must not be empty');
        require(bytes(_description).length > 0, 'Description must not be empty');

        // Create a new chama group with the provided information and add it to the array
        Group memory newGroup = Group({
            id : nextId,  // assign a unique id to the new chama group
            creator : msg.sender, // set the creator as the current sender of the transaction
            name : _name, // set the name of the chama group as the provided name
            description : _description, // set the description of the chama group as the provided description
            status : STATUS.ACTIVE, // set the status of the chama group as ACTIVE
            totalContributions : 0, // set the total contributions to 0 as no contributions have been made yet
            contributors : new address[](0), // create an empty array to store contributors
            contributionAmounts : new uint[](0) // create an empty array to store contribution amounts
        });

        // Create a new array with the creator as the first contributor
        address[] memory contributorsWithCreator = new address[](1);
        contributorsWithCreator[0] = msg.sender;

        // Assign the new array to the group's contributors
        newGroup.contributors = contributorsWithCreator;

        groups[nextId] = newGroup; // add the new chama group to the array

        // Track the group in the contributorGroups mapping
        contributorGroups[newGroup.creator].push(newGroup.id);

        // emit an event to notify listeners that a new chama group has been created
        emit GroupCreated(newGroup.id, newGroup.creator, newGroup.name, newGroup.status);

        // increment the id counter to generate unique ids for new chama groups
        nextId++;
    }

    // Function that allows the group creator to delette the group.
    function deleteGroup(uint groupId)  public {
        // Retrieve the group based on the group id
        Group storage group = groups[groupId];

        // Validatiion check: only creator can delete
        require(group.creator == msg.sender, "Only creator can delete");

        // TODO: Refund the contriibutors when group is delete

        // Remove the group from each contributor's list
        for (uint i = 0; i < group.contributors.length; i++) {
            address contributor = group.contributors[i];
            uint[] storage contributorGroupsList = contributorGroups[contributor];

            // Find the index of the group in the contributor's list
            for (uint j = 0; j < contributorGroupsList.length; j++) {
                if (contributorGroupsList[j] == groupId) {
                    // Remove the group from the list by shifting elements
                    for (uint k = j; k < contributorGroupsList.length - 1; k++) {
                        contributorGroupsList[k] = contributorGroupsList[k + 1];
                    }
                    contributorGroupsList.pop();
                    break;
                }
            }
        }
        // Mark group as delete
        group.status = STATUS.DELETED;

        // emit an event to notify listeners that the chama group has been deleted
        emit GroupDeleted(groupId, msg.sender, STATUS.DELETED);
    }

    // Function to get a group by its ID
    function getGroupById(uint groupId) public view returns (
        uint id,
        address creator,
        string memory name,
        string memory description,
        STATUS status,
        uint totalContributions
    ) {
        require(groups[groupId].id > 0, "Group does not exist");
        require(groups[groupId].status == STATUS.ACTIVE, "Group is not active");

        Group storage group = groups[groupId];
        return (
            group.id,
            group.creator,
            group.name,
            group.description,
            group.status,
            group.totalContributions
        );
    }


    // Function to get all groups that a specific contributor is part of
    function getGroupsByContributor(address contributor) public view returns (uint[] memory) {
        return contributorGroups[contributor];
    }

    // Add a contributor to a group
    function addContributor(uint groupId, address newContributor) public {
        Group storage group = groups[groupId];
        require(group.status == STATUS.ACTIVE, "Group is not active");
        // Validatiion check: only creator can add a contributor
        require(group.creator == msg.sender, "Only group creator can add contributors");

        // Prevent duplicate contributors
        require(findContributorIndex(group.contributors, newContributor) == type(uint).max, "Contributor exists");


        group.contributors.push(newContributor);
        contributorGroups[newContributor].push(groupId);

        emit ContributorAdded(groupId, newContributor);
    }

    // Remove a contributor from a group
    function removeContributor(uint groupId, address contributor) public {
        Group storage group = groups[groupId];
        require(group.status == STATUS.ACTIVE, "Group inactive");
        require(msg.sender == group.creator, "Only creator");

        uint index = findContributorIndex(group.contributors, contributor);
        require(index < group.contributors.length, "Contributor not found");

        // Safe array manipulation
        group.contributors[index] = group.contributors[group.contributors.length - 1];
        group.contributors.pop();

        // Assuming contributionAmounts has the same length as contributors
        group.contributionAmounts[index] = group.contributionAmounts[group.contributionAmounts.length - 1];
        group.contributionAmounts.pop();

        // Remove from contributor's groups
        uint[] storage cGroups = contributorGroups[contributor];
        for (uint i = 0; i < cGroups.length; i++) {
            if (cGroups[i] == groupId) {
                cGroups[i] = cGroups[cGroups.length - 1];
                cGroups.pop();
                break;
            }
        }
    }

    // Helper function to find contributor index
    function findContributorIndex(address[] memory array, address value) private pure returns (uint) {
        for (uint i = 0; i < array.length; i++) {
            if (array[i] == value) {
                return i;
            }
        }
        return type(uint).max;
    }

}

contracts/ChamaGroup2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";


// TODO add membership contract - use Unlock Protocol for the production version? More research needed

contract ChamaGroupOwnable is ReentrancyGuard, Ownable {
    using SafeMath for uint256;

    uint private nextId = 1;

    enum STATUS {
        ACTIVE,
        DELETED
    }


    // TODO update the group struct to better match a group
    // TODO - add a set contribution amount,
    // TODO - add a set contribution timeline [ Date]
    // TODO - add set savings split / ratio
    // TODO - implement a selected grantee
    // TODO - implement grantee selection method select the minimum period required
    // TODO - verify membership before allowing contributions (Attest), currently the implementations is incorrect
    // TODO - add approve membership
    // what is a stokvel group? - 
    struct Group {
        uint id;
        address creator;
        string name;
        string description;
        STATUS status;
        uint totalEtherContributions;
        address[] contributedTokens; // Array to track token addresses that have been contributed
        mapping(address => uint) totalTokenContributions; // token address => total contribution
        mapping(address => uint) etherContributions;
        mapping(address => mapping(address => uint)) tokenContributions; // contributor => token address => amount
        mapping(address => bool) isContributor;
        uint contributorCount;
        address[] contributors; // To safely iterate over contributors
    }

    mapping(uint => Group) private groups;
    mapping(address => uint[]) private contributorGroups;
    mapping(address => uint) private pendingEtherWithdrawals;
    mapping(address => mapping(address => uint)) private pendingTokenWithdrawals;

    event GroupCreated(uint indexed groupId, address groupCreator, string name, STATUS status);
    event GroupDeleted(uint indexed groupId, address groupCreator, STATUS status);
    event ContributorAdded(uint indexed groupId, address indexed newContributor);
    event ContributorRemoved(uint indexed groupId, address indexed contributor);
    event EtherWithdrawal(address indexed contributor, uint amount);
    event TokenWithdrawal(address indexed contributor, address token, uint amount);
    event TokenContribution(address indexed contributor, uint indexed groupId, address token, uint amount);

    constructor() {
        // Owner is set by Ownable
    }

    modifier groupExists(uint groupId) {
        require(groups[groupId].id != 0, "Group does not exist");
        _;
    }

    modifier onlyGroupCreator(uint groupId) {
        require(msg.sender == groups[groupId].creator, "Only the group creator can perform this action");
        _;
    }

    function createGroup(
        string memory _name,
        string memory _description
    ) public nonReentrant {
        require(bytes(_name).length > 0, "Name must not be empty");
        require(bytes(_description).length > 0, "Description must not be empty");

        Group storage newGroup = groups[nextId];
        newGroup.id = nextId;
        newGroup.creator = msg.sender;
        newGroup.name = _name;
        newGroup.description = _description;
        newGroup.status = STATUS.ACTIVE;
        newGroup.contributorCount = 1;


        // we use a 
        newGroup.isContributor[msg.sender] = true;
        newGroup.etherContributions[msg.sender] = 0;
        newGroup.contributors.push(msg.sender);

        contributorGroups[msg.sender].push(nextId);

        emit GroupCreated(nextId, msg.sender, _name, STATUS.ACTIVE);

        nextId++;
    }

    function contributeEther(uint groupId) public payable nonReentrant groupExists(groupId) {
        Group storage group = groups[groupId];
        require(group.status == STATUS.ACTIVE, "Group is not active");

        group.etherContributions[msg.sender] = group.etherContributions[msg.sender].add(msg.value);
        group.totalEtherContributions = group.totalEtherContributions.add(msg.value);

        // TODO we can only be allowed t contribute after the group members attest and confirm our membership
        // the below logic is thus flawed
        if (!group.isContributor[msg.sender]) {
            group.isContributor[msg.sender] = true;
            group.contributorCount++;
            group.contributors.push(msg.sender);
            contributorGroups[msg.sender].push(groupId);
            emit ContributorAdded(groupId, msg.sender);
        }
    }

    function contributeToken(uint groupId, address tokenAddress, uint amount) public nonReentrant groupExists(groupId) {
        require(tokenAddress != address(0), "Invalid token address");
        Group storage group = groups[groupId];
        require(group.status == STATUS.ACTIVE, "Group is not active");

        IERC20 token = IERC20(tokenAddress);
        require(token.transferFrom(msg.sender, address(this), amount), "Token transfer failed");

        group.tokenContributions[msg.sender][tokenAddress] = group.tokenContributions[msg.sender][tokenAddress].add(amount);
        group.totalTokenContributions[tokenAddress] = group.totalTokenContributions[tokenAddress].add(amount);

        // Track the token address if it's the first time it is contributed
        if (group.totalTokenContributions[tokenAddress] == amount) {
            group.contributedTokens.push(tokenAddress);
        }

        // TODO we can only be allowed t contribute after the group members attest and confirm our membership
        // TODO make a new function to verify membership before allowing contributions, currently the implementations is incorrect
        if (!group.isContributor[msg.sender]) {
            group.isContributor[msg.sender] = true;
            group.contributorCount++;
            group.contributors.push(msg.sender);
            contributorGroups[msg.sender].push(groupId);
            emit ContributorAdded(groupId, msg.sender);
        }

        emit TokenContribution(msg.sender, groupId, tokenAddress, amount);
    }

    function deleteGroup(uint groupId) public nonReentrant onlyGroupCreator(groupId) groupExists(groupId) {
        Group storage group = groups[groupId];
        require(group.status == STATUS.ACTIVE, "Group is not active");

        for (uint i = 0; i < group.contributors.length; i++) {
            address contributor = group.contributors[i];
            uint etherAmount = group.etherContributions[contributor];
            if (etherAmount > 0) {
                pendingEtherWithdrawals[contributor] = pendingEtherWithdrawals[contributor].add(etherAmount);
            }

            // Iterate over contributed tokens
            for (uint j = 0; j < group.contributedTokens.length; j++) {
                address tokenAddr = group.contributedTokens[j];
                uint tokenAmount = group.tokenContributions[contributor][tokenAddr];
                if (tokenAmount > 0) {
                    pendingTokenWithdrawals[tokenAddr][contributor] = pendingTokenWithdrawals[tokenAddr][contributor].add(tokenAmount);
                }
            }
        }

        group.status = STATUS.DELETED;

        emit GroupDeleted(groupId, msg.sender, STATUS.DELETED);
    }

    function withdrawEther() public nonReentrant {
        uint amount = pendingEtherWithdrawals[msg.sender];
        require(amount > 0, "No Ether to withdraw");
        require(address(this).balance >= amount, "Insufficient contract balance");

        pendingEtherWithdrawals[msg.sender] = 0;

        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Ether withdrawal failed");

        emit EtherWithdrawal(msg.sender, amount);
    }

    // do we need to ensure the correct wei units?

    function withdrawToken(address tokenAddress) public nonReentrant {
        uint amount = pendingTokenWithdrawals[tokenAddress][msg.sender];
        require(amount > 0, "No tokens to withdraw");

        pendingTokenWithdrawals[tokenAddress][msg.sender] = 0;

        IERC20 token = IERC20(tokenAddress);
        require(token.transfer(msg.sender, amount), "Token withdrawal failed");

        emit TokenWithdrawal(msg.sender, tokenAddress, amount);
    }

    function getGroupById(uint groupId) public view groupExists(groupId) returns (
        uint id,
        address creator,
        string memory name,
        string memory description,
        STATUS status,
        uint totalEtherContributions,
        uint contributorCount
    ) {
        Group storage group = groups[groupId];
        require(group.status == STATUS.ACTIVE, "Group is not active");

        return (
            group.id,
            group.creator,
            group.name,
            group.description,
            group.status,
            group.totalEtherContributions,
            group.contributorCount
        );
    }

    function getGroupsByContributor(address contributor) public view returns (uint[] memory) {
        return contributorGroups[contributor];
    }

    fallback() external payable {
        revert("Fallback function triggered, no ether accepted");
    }

    receive() external payable {
        revert("Contract does not accept direct ether payments");
    }
}

contracts/ChamaGroup3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Address.sol";

/**
 * @title ChamaGroupV3
 * @dev A simplified proof-of-concept smart contract for managing savings groups (Chamas) where members can contribute Ether or ERC20 tokens.
 * The contract includes basic functionalities for group creation, contributions, grantee selection, and withdrawals.
 */
contract ChamaGroupV3 is ReentrancyGuard, Ownable {
    using Counters for Counters.Counter;
    using SafeERC20 for IERC20;
    using Address for address payable;

    Counters.Counter nextId;

    enum STATUS {
        ACTIVE,
        DELETED
    }

    struct Group {
        uint id;
        address creator;
        string name;
        string description;
        STATUS status;
        uint contributionAmount;
        uint contributionTimeline;
        uint savingsSplit; // Percentage out of 10000 (e.g., 5000 represents 50%)
        uint lastContributionTime;
        address[] contributors;
        mapping(address => uint256) etherContributions;
        mapping(address => mapping(address => uint256)) tokenContributions;
        mapping(address => bool) approvedMembers;
        mapping(address => uint256) votingPower;
        address selectedGrantee;
        address[] allowedTokens;
        uint totalContributions;
        bool granteeApproved; // Tracks if the grantee is approved to withdraw
        bool hasWithdrawn; // Tracks if the grantee has withdrawn in the current period
    }

    mapping(uint => Group) groups;
    mapping(address => uint[]) contributorGroups;
     uint256 public maxBatchSize = 100;

    event GroupCreated(uint indexed groupId, address groupCreator, string name, string description, STATUS status);
    event ContributionMade(uint indexed groupId, address indexed contributor, uint amount, address tokenAddress);
    event MemberApproved(uint indexed groupId, address indexed member);
    event GranteeSelected(uint indexed groupId, address indexed grantee);
    event GroupDeleted(uint indexed groupId, address groupCreator, STATUS status);
    event Withdrawal(address indexed contributor, uint amount, address tokenAddress);
    event MemberAddedByOwner(uint indexed groupId, address indexed member);
    event MembersAddedByOwner(uint indexed groupId, uint numberOfMembers);
    event MemberRevoked(uint indexed groupId, address indexed member);

    constructor() {
        nextId.increment(); // Start IDs from 1
    }

    modifier groupExists(uint groupId) {
        require(groups[groupId].id != 0, "Group does not exist");
        _;
    }

    modifier onlyGroupCreator(uint groupId) {
        require(msg.sender == groups[groupId].creator, "Only the group creator can perform this action");
        _;
    }

    modifier onlyApprovedMember(uint groupId) {
        require(groups[groupId].approvedMembers[msg.sender], "You are not an approved member");
        _;
    }

    modifier onlySelectedGrantee(uint groupId) {
        require(msg.sender == groups[groupId].selectedGrantee, "You are not the selected grantee");
        _;
    }

    /**
     * @dev Creates a new savings group with the specified parameters.
     * @param _name The name of the group.
     * @param _description The description of the group.
     * @param _contributionAmount The fixed contribution amount for all members, denoted in the smallest unit (e.g., wei for Ether, or token decimals for ERC20).
     * @param _contributionTimeline The timeline in seconds for contributions.
     * @param _savingsSplit The percentage out of 10000 for the savings split.
     * @param _allowedTokens An array of allowed ERC20 token addresses for contributions.
     */
    function createGroup(
        string memory _name,
        string memory _description,
        uint _contributionAmount,
        uint _contributionTimeline,
        uint _savingsSplit,
        address[] memory _allowedTokens
    ) public nonReentrant {
        require(bytes(_name).length > 0, "Name must not be empty");
        require(bytes(_description).length > 0, "Description must not be empty");
        require(_contributionAmount > 0, "Contribution amount must be greater than zero");
        require(_contributionTimeline > 0, "Contribution timeline must be greater than zero");
        require(_savingsSplit <= 10000, "Savings split must be a percentage (0-10000)");

        Group storage newGroup = groups[nextId.current()];
        newGroup.id = nextId.current();
        newGroup.creator = msg.sender;
        newGroup.name = _name;
        newGroup.description = _description;
        newGroup.status = STATUS.ACTIVE;
        newGroup.contributionAmount = _contributionAmount;
        newGroup.contributionTimeline = _contributionTimeline;
        newGroup.savingsSplit = _savingsSplit;
        newGroup.contributors.push(msg.sender);
        newGroup.approvedMembers[msg.sender] = true;
        newGroup.allowedTokens = _allowedTokens;

        contributorGroups[msg.sender].push(nextId.current());

        emit GroupCreated(nextId.current(), msg.sender, _name, _description, STATUS.ACTIVE);

        nextId.increment();
    }

    /**
     * @dev Approves a new member to join the specified group.
     * @param groupId The ID of the group.
     * @param newMember The address of the new member.
     */
    function approveMember(uint groupId, address newMember) public nonReentrant groupExists(groupId) onlyGroupCreator(groupId) {
        _approveMemberInternal(groupId, newMember);
    }

    /**
     * @dev Allows the contract owner to add a new member to a group.
     * @param groupId The ID of the group.
     * @param newMember The address of the new member.
     */
    function addMemberByOwner(uint groupId, address newMember) public nonReentrant onlyGroupCreator(groupId) groupExists(groupId) {
        _approveMemberInternal(groupId, newMember);
        emit MemberAddedByOwner(groupId, newMember);
    }

    /**
     * @dev Allows the contract owner to add new members to a group.
     * @param groupId The ID of the group.
     * @param newMembers The address of the new member.
     */
    function addMembersByOwner(uint groupId, address[] memory newMembers) public nonReentrant onlyGroupCreator(groupId) groupExists(groupId) {
        require(newMembers.length > 0, "No members provided.");
        require(newMembers.length <= maxBatchSize, "Batch size exceeds maximum allowed for new members.");

        for (uint i = 0; i < newMembers.length; i++) {
          groups[groupId].contributors.push(newMembers[i]);
          groups[groupId].approvedMembers[newMembers[i]] = false; // set to false by default
          }
        emit MembersAddedByOwner(groupId, newMembers.length);
    }

    /**
     * @dev Allows the contract owner to revoke member's approval status.
     * @param groupId The ID of the group.
     * @param member The address of the revoked member.
     */

    function revokeMemberApproval(uint groupId, address member) public nonReentrant onlyGroupCreator(groupId) groupExists(groupId) {
        Group storage group = groups[groupId];
        group.approvedMembers[member] = false;

        emit MemberRevoked(groupId, member);
    }

    /**
     * @dev Contributes Ether or ERC20 tokens to the specified group.
     * @param groupId The ID of the group.
     * @param tokenAddress The address of the ERC20 token (use 0x0000000000000000000000000000000000000000 for Ether).
     * @param tokenAmount The amount of ERC20 tokens to contribute, in the token's smallest unit.
     */
    function contribute(uint groupId, address tokenAddress, uint tokenAmount) public payable nonReentrant groupExists(groupId) onlyApprovedMember(groupId) {
        Group storage group = groups[groupId];
        require(group.status == STATUS.ACTIVE, "Group is not active");
        require(block.timestamp >= group.lastContributionTime + group.contributionTimeline, "Contribution timeline not met");
        require(block.timestamp < group.lastContributionTime + group.contributionTimeline + 1 days, "Contribution period has ended");

        bool isEther = tokenAddress == address(0);
        uint256 valueToGrantee = (group.savingsSplit * group.contributionAmount) / 10000;
        uint256 valueToSave = group.contributionAmount - valueToGrantee;

        if (isEther) {
            require(msg.value == group.contributionAmount, "Incorrect Ether contribution amount");
            group.etherContributions[msg.sender] += valueToSave;
            group.etherContributions[group.selectedGrantee] += valueToGrantee;
        } else {
            require(tokenAmount == group.contributionAmount, "Incorrect token contribution amount");
            require(isAllowedToken(group, tokenAddress), "Token not allowed for this group");

            // Safe transfer with try/catch for non-standard tokens
            IERC20(tokenAddress).safeTransferFrom(msg.sender, address(this), tokenAmount);
            group.tokenContributions[msg.sender][tokenAddress] += valueToSave;
            group.tokenContributions[group.selectedGrantee][tokenAddress] += valueToGrantee;
        }

        group.totalContributions += group.contributionAmount;
        group.lastContributionTime = block.timestamp;
        group.granteeApproved = true; // Approve the grantee for withdrawal
        group.hasWithdrawn = false; // Reset the withdrawal flag for the new period

        if (!isContributor(group, msg.sender)) {
            group.contributors.push(msg.sender);
            contributorGroups[msg.sender].push(groupId);
        }

        // Update voting power based on contribution
        group.votingPower[msg.sender] += valueToGrantee;

        emit ContributionMade(groupId, msg.sender, isEther ? msg.value : tokenAmount, tokenAddress);

        // Automatically select the next grantee after each contribution
        selectGranteeByVoting(groupId);
    }

    /**
     * @dev Selects the next grantee using a weighted voting system.
     * Only approved members can vote.
     * @param groupId The ID of the group.
     */
    function selectGranteeByVoting(uint groupId) internal groupExists(groupId) {
        Group storage group = groups[groupId];
        uint256 highestVotes = 0;
        address newGrantee;

        for (uint i = 0; i < group.contributors.length; i++) {
            address contributor = group.contributors[i];
            if (group.approvedMembers[contributor] && group.votingPower[contributor] > highestVotes) {
                highestVotes = group.votingPower[contributor];
                newGrantee = contributor;
            }
        }

        group.selectedGrantee = newGrantee;

        emit GranteeSelected(groupId, group.selectedGrantee);
    }

    /**
     * @dev Withdraws Ether or ERC20 tokens for the selected grantee.
     * Only the grantee can withdraw, and only once per contribution period.
     * @param groupId The ID of the group.
     * @param tokenAddress The address of the ERC20 token (use 0x0000000000000000000000000000000000000000 for Ether).
     */
    function withdraw(uint groupId, address tokenAddress) public nonReentrant groupExists(groupId) onlySelectedGrantee(groupId) {
        Group storage group = groups[groupId];
        require(group.granteeApproved, "Grantee withdrawal not approved");
        require(!group.hasWithdrawn, "Withdrawal already made in this period");
        require(group.status == STATUS.ACTIVE, "Group is not active");

        bool isEther = tokenAddress == address(0);
        uint256 amount;

        if (isEther) {
            amount = group.etherContributions[msg.sender];
            require(amount > 0, "No Ether to withdraw");
            group.etherContributions[msg.sender] = 0;
            payable(msg.sender).sendValue(amount);
        } else {
            amount = group.tokenContributions[msg.sender][tokenAddress];
            require(amount > 0, "No tokens to withdraw");
            group.tokenContributions[msg.sender][tokenAddress] = 0;

            // Safe transfer with try/catch for non-standard tokens
            IERC20(tokenAddress).safeTransfer(msg.sender, amount);
        }

        group.hasWithdrawn = true; // Mark the withdrawal as completed for the period
        group.granteeApproved = false; // Revoke approval after withdrawal

        emit Withdrawal(msg.sender, amount, tokenAddress);
    }

    /**
     * @dev Deletes a group and marks it as inactive.
     * @param groupId The ID of the group.
     */
    function deleteGroup(uint groupId) public nonReentrant groupExists(groupId) onlyGroupCreator(groupId) {
        Group storage group = groups[groupId];
        require(group.status == STATUS.ACTIVE, "Group is not active");

        group.status = STATUS.DELETED;

        emit GroupDeleted(groupId, msg.sender, STATUS.DELETED);
    }

    /**
     * @dev Retrieves the details of a group by its ID.
     * @param groupId The ID of the group.
     * @return id The ID of the group.
     * @return creator The creator of the group.
     * @return name The name of the group.
     * @return description The description of the group.
     * @return status The current status of the group.
     * @return contributionAmount The fixed contribution amount for the group.
     * @return contributionTimeline The contribution timeline for the group.
     * @return savingsSplit The savings split percentage for the group.
     * @return selectedGrantee The currently selected grantee.
     */
    function getGroupById(uint groupId) public view groupExists(groupId) returns (
        uint id,
        address creator,
        string memory name,
        string memory description,
        STATUS status,
        uint contributionAmount,
        uint contributionTimeline,
        uint savingsSplit,
        address selectedGrantee
    ) {
        Group storage group = groups[groupId];

        return (
            group.id,
            group.creator,
            group.name,
            group.description,
            group.status,
            group.contributionAmount,
            group.contributionTimeline,
            group.savingsSplit,
            group.selectedGrantee
        );
    }

    /**
     * @dev Retrieves the IDs of groups a contributor belongs to.
     * @param contributor The address of the contributor.
     * @return An array of group IDs the contributor belongs to.
     */
    function getGroupsByContributor(address contributor) public view returns (uint[] memory) {
        return contributorGroups[contributor];
    }

    /**
     * @dev Checks if a token is allowed for a group.
     * @param group The group to check.
     * @param tokenAddress The address of the token to check.
     * @return True if the token is allowed, false otherwise.
     */
    function isAllowedToken(Group storage group, address tokenAddress) internal view returns (bool) {
        for (uint i = 0; i < group.allowedTokens.length; i++) {
            if (group.allowedTokens[i] == tokenAddress) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev Checks if an address is a contributor in the group.
     * @param group The group to check.
     * @param contributor The address to check.
     * @return True if the address is a contributor, false otherwise.
     */
    function isContributor(Group storage group, address contributor) internal view returns (bool) {
        for (uint i = 0; i < group.contributors.length; i++) {
            if (group.contributors[i] == contributor) {
                return true;
            }
        }
        return false;
    }

    /**
     * @dev Internal function to approve a new member to join the specified group.
     * @param groupId The ID of the group.
     * @param newMember The address of the new member.
     */
    function _approveMemberInternal(uint groupId, address newMember) internal {
        Group storage group = groups[groupId];
        group.approvedMembers[newMember] = true;

        emit MemberApproved(groupId, newMember);
    }

    /**
     * @dev Fallback function that reverts any Ether sent to the contract.
     */
    fallback() external payable {
        revert("Fallback function triggered, no Ether accepted");
    }

    /**
     * @dev Receive function that reverts any direct Ether payments.
     */
    receive() external payable {
        revert("Contract does not accept direct Ether payments");
    }
}

